function fillProductModal(i){let n="";n+=`\n      \x3c!-- Start product images --\x3e\n      <div class="product-images">\n         <div class="main-image images">\n            <img alt="big images" src="assets/${i.image_big}">\n         </div>\n      </div>\n      \x3c!-- end product images --\x3e\n      <div class="product-info">\n         <h1>${i.name}</h1>\n         <div class="rating__and__review">\n            <div class="rating">\n               Color:\n            </div>\n            <div class="review">\n               <a href="">${i.color}</a>\n            </div>\n         </div>\n         <div class="price-box-3">\n            <div class="s-price-box">\n               <span class="new-price">&dollar;${i.price}</span>`,"object"!=typeof i.price_old&&(n+=`\n               <span class="old-price">&dollar;${i.price_old}</span>`),n+=`\n            </div>\n         </div>\n         <div class="quick-desc">\n            ${i.description}\n         </div>\n      </div>\n\n\n      `,$("#modalProduct").html(n)}$(document).on("click",".detail-link",function(){let i=$(this).data("id");$.ajax({url:"models/products/filter_product_modal.php",method:"get",dataType:"json",data:{_id:i},success:function(i){fillProductModal(i)},error:function(i,n,d){console.log(n)}})});
